select
order_id
from completedorder


    and TimeMillis >#{maxtime,jdbcType=BIGINT}


            and app_poi_code in

             #{appPoiCode,jdbcType=VARCHAR}




        limit #{start},#{size}
    ;

select count(*) from completedorder


and TimeMillis >#{maxtime,jdbcType=BIGINT}


        and app_poi_code in

         #{appPoiCode,jdbcType=VARCHAR}

